INSERT INTO `software_actionators` VALUES (2,56,'list_users','#!/bin/bash\r\n\r\necho \"select user from prosody where store=\'accounts\';\" |mysql  --column-names=0 -h $dbhost -u $dbuser --password=$dbpasswd $dbname \r\n','List Users','','plain_text',NULL,NULL),(3,56,'add_user','#!/bin/bash\r\necho $1 | /home/engines/bin/json_to_env  >/tmp/add_user.env \r\n. /tmp/add_user.env\r\n\r\nprosodyctl register $username $Domainname $password 1>/tmp/add_user.err\r\nif test $? -eq 0\r\n then\r\n  echo  Successfully added $username@$Domainname  \r\n  rm  /tmp/add_user.env\r\n  exit 0\r\n fi\r\n\r\necho \"Failed to add $username@$Domainname  \"\r\ncat /tmp/add_user.err\r\nexit -1','Add user','','plain_text',NULL,NULL),(4,56,'del_user','#!/bin/bash\r\n\r\necho $1 |/home/engines/bin/json_to_env >/tmp/del_user.env\r\n. /tmp/del_user.env\r\n\r\nprosodyctl deluser $username@$Domainname 1>/tmp/del_user.err\r\n if test $? -eq 0\r\n  then\r\n      rm  /tmp/del_user.env\r\n      echo  Sucessfully deleted $username@$Domainname\r\n      exit 0\r\n  fi\r\n\r\necho Failed to delete $username@$Domainname\r\ncat /tmp/del_user.err\r\nexit -1\r\n','Delete User','','plain_text',NULL,NULL),(5,56,'change_password','#!/bin/bash\r\n\r\necho $1 |/home/engines/bin/json_to_env >/tmp/chg_pass.env\r\n. /tmp/chg_pass.env\r\necho $password >/tmp/t\r\necho $password >>/tmp/t\r\n\r\ncat /tmp/t | prosodyctl passwd $username@$Domainname 1>/tmp/changepass.err\r\nif test $? -eq 0 \r\nthen\r\n   echo Sucessfully changed password for $username@$Domainname\r\n   rm /tmp/chg_pass.env\r\n   rm /tmp/t\r\n   exit 0\r\nfi\r\nrm /tmp/t\r\necho Failed\r\ncat /tmp/changepass.err\r\nexit -1\r\n','Change Users Password','','plain_text',NULL,0),(6,84,'update_code','#!/bin/bash\r\ncd /home/app\r\ngit pull','Update Code','','plain_text',NULL,0),(7,84,'db_migrate','#!/bin/bash\r\ncd /home/app\r\nrake db:migrate','Migrate db','','plain_text',NULL,0),(8,85,'update_code','cd /home/app\r\ngit pull','','','plain_text',NULL,0);
