lanks. Good luck, and happy Jabbering!\r\n\r\npidfile = \"/tmp/prosody.pid\"\r\n\r\n---------- Server-wide settings ----------\r\n-- Settings in this section apply to the whole server and are the default settings\r\n-- for any virtual hosts\r\n\r\n-- This is a (by default, empty) list of accounts that are admins\r\n-- for the server. Note that you must create the accounts separately\r\n-- (see http://prosody.im/doc/creating_accounts for info)\r\n-- Example: admins = { \"user1@example.com\", \"user2@example.net\" }\r\nadmins = {\"_Engines_Environment(admin_user)@_Engines_Builder(domain_name)\" }\r\n\r\n-- Enable use of libevent for better performance under high load\r\n-- For more information see: http://prosody.im/doc/libevent\r\n--use_libevent = true;\r\n\r\n-- This is the list of modules Prosody will load on startup.\r\n-- It looks for mod_modulename.lua in the plugins folder, so make sure that exists too.\r\n-- Documentation on modules can be found at: http://prosody.im/doc/modules\r\nmodules_enabled = {\r\n\r\n	-- Generally required\r\n		\"roster\"; -- Allow users to have a roster. Recommended ;)\r\n		\"saslauth\"; -- Authentication for clients and servers. Recommended if you want to log in.\r\n		\"tls\"; -- Add support for secure TLS on c2s/s2s connections\r\n		\"dialback\"; -- s2s dialback support\r\n		\"disco\"; -- Service discovery\r\n\r\n	-- Not essential, but recommended\r\n		\"private\"; -- Private XML storage (for room bookmarks, etc.)\r\n		\"vcard\"; -- Allow users to set vCards\r\n	\r\n	-- These are commented by default as they have a performance impact\r\n		--\"privacy\"; -- Support privacy lists\r\n		--\"compression\"; -- Stream compression\r\n\r\n	-- Nice to have\r\n		\"version\"; -- Replies to server version requests\r\n		\"uptime\"; -- Report how long server has been running\r\n		\"time\"; -- Let others know the time here on this server\r\n		\"ping\"; -- Replies to XMPP pings with pongs\r\n		\"pep\"; -- Enables users to publish their mood, activity, playing music and more\r\n		\"register\"; -- Allow users to register on this server using a client and change passwords\r\n\r\n	-- Admin interfaces\r\n		\"admin_adhoc\"; -- Allows administration via an XMPP client that supports ad-hoc commands\r\n		--\"admin_telnet\"; -- Opens telnet console interface on localhost port 5582\r\n	\r\n	-- HTTP modules\r\n		--\"bosh\"; -- Enable BOSH clients, aka \"Jabber over HTTP\"\r\n		--\"http_files\"; -- Serve static files from a directory over HTTP\r\n\r\n	-- Other specific functionality\r\n		--\"posix\"; -- POSIX functionality, sends server to background, enables syslog, etc.\r\n		--\"groups\"; -- Shared roster support\r\n		--\"announce\"; -- Send announcement to all online users\r\n		--\"welcome\"; -- Welcome users who register accounts\r\n		--\"watchregistrations\"; -- Alert admins of registrations\r\n		--\"motd\"; -- Send a message to users when they log in\r\n		--\"legacyauth\"; -- Legacy authentication. Only used by some old clients and bots.\r\n};\r\n\r\n-- These modules are auto-loaded, but should you want\r\n-- to disable them then uncomment them here:\r\nmodules_disabled = {\r\n	-- \"offline\"; -- Store offline messages\r\n	-- \"c2s\"; -- Handle client connections\r\n	-- \"s2s\"; -- Handle server-to-server connections\r\n};\r\n\r\n-- Disable account creation by default, for security\r\n-- For more information see http://prosody.im/doc/creating_accounts\r\nallow_registration = false;\r\n\r\n-- These are the SSL/TLS-related settings. If you don\'t want\r\n-- to use SSL/TLS, you may comment or remove this\r\nssl = {\r\n	key = \"/usr/local/etc/prosody/certs/localhost.key\";\r\n	certificate = \"/usr/local/etc/prosody/certs/localhost.crt\";\r\n}\r\n\r\n-- Force clients to use encrypted connections? This option will\r\n-- prevent clients from authenticating unless they are using encryption.\r\n\r\nc2s_require_encryption = false\r\n\r\n-- Force certificate authentication for server-to-server connections?\r\n-- This provides ideal security, but requires servers you communicate\r\n-- with to support encryption AND present valid, trusted certificates.\r\n-- NOTE: Your version of LuaSec must support certificate verification!\r\n-- For more information see http://prosody.im/doc/s2s#security\r\n\r\ns2s_secure_auth = false\r\n\r\n-- Many servers don\'t support encryption or have invalid or self-signed\r\n-- certificates. You can list domains here that will not be required to\r\n-- authenticate using certificates. They will be authenticated using DNS.\r\n\r\n--s2s_insecure_domains = { \"gmail.com\" }\r\n\r\n-- Even if you leave s2s_secure_auth disabled, you can still require valid\r\n-- certificates for some domains by specifying a list here.\r\n\r\n--s2s_secure_domains = { \"jabber.org\" }\r\n\r\n-- Select the authentication backend to use. The \'internal\' providers\r\n-- use Prosody\'s configured data storage to store the authentication data.\r\n-- To allow Prosody to offer secure authentication mechanisms to clients, the\r\n-- default provider stores passwords in plaintext. If you do not trust your\r\n-- server please see http://prosody.im/doc/modules/mod_auth_internal_hashed\r\n-- for information about using the hashed backend.\r\n\r\nauthentication = \"internal_plain\"\r\n\r\n-- Select the storage backend to use. By default Prosody uses flat files\r\n-- in its configured data directory, but it also supports more backends\r\n-- through modules. An \"sql\" backend is included by default, but requires\r\n-- additional dependencies. See http://prosody.im/doc/storage for more info.\r\n\r\nstorage = \"sql\" -- Default is \"internal\"\r\n\r\n-- For the \"sql\" backend, you can uncomment *one* of the below to configure:\r\n--sql = { driver = \"SQLite3\", database = \"prosody.sqlite\" } -- Default. \'database\' is the filename.\r\nsql = { driver = \"MySQL\", database = \"_Engines_Environment(dbname)\", username = \"_Engines_Environment(dbuser)\", password = \"_Engines_Environment(dbpasswd)\", host = \"_Engines_Environment(dbhost)\" }\r\n--sql = { driver = \"PostgreSQL\", database = \"prosody\", username = \"prosody\", password = \"secret\", host = \"localhost\" }\r\n\r\n-- Logging configuration\r\n-- For advanced logging see http://prosody.im/doc/logging\r\nlog = {\r\n	info = \"prosody.log\"; -- Change \'info\' to \'debug\' for verbose logging\r\n	error = \"prosody.err\";\r\n	\"*syslog\"; -- Uncomment this for logging to syslog\r\n	-- \"*console\"; -- Log to the console, useful for debugging with daemonize=false\r\n}\r\n\r\n----------- Virtual hosts -----------\r\n-- You need to add a VirtualHost entry for each domain you wish Prosody to serve.\r\n-- Settings under each VirtualHost entry apply *only* to that host.\r\n\r\nVirtualHost \"localhost\"\r\n\r\nVirtualHost \"_Engines_Builder(domain_name)\"\r\n	-- enabled = false -- Remove this line to enable this host\r\nenabled = true\r\n	-- Assign this host a certificate for TLS, otherwise it would use the one\r\n	-- set in the global section (if any).\r\n	-- Note that old-style SSL on port 5223 only supports one certificate, and will always\r\n	-- use the global one.\r\n	ssl = {\r\n		key = \"/usr/local/etc/prosody/certs/example.com.key\";\r\n		certificate = \"/usr/local/etc/prosody/certs/example.com.crt\";\r\n	}\r\n\r\n------ Components ------\r\n-- You can specify components to add hosts that provide special services,\r\n-- like multi-user conferences, and transports.\r\n-- For more information on components, see http://prosody.im/doc/components\r\n\r\n---Set up a MUC (multi-user chat) room server on conference.example.com:\r\n--Component \"conference.example.com\" \"muc\"\r\n\r\n-- Set up a SOCKS5 bytestream proxy for server-proxied file transfers:\r\n--Component \"proxy.example.com\" \"proxy65\"\r\n\r\n---Set up an external component (default component port is 5347)\r\n--\r\n-- External components allow adding various services, such as gateways/\r\n-- transports to other networks like ICQ, MSN and Yahoo. For more info\r\n-- see: http://prosody.im/doc/components#adding_an_external_component\r\n--\r\n--Component \"gateway.example.com\"\r\n--	component_secret = \"password\"\r\n',56,'2016-03-06 02:50:43','2016-03-06 06:11:12'),(67,'configtemplate','app/protected/install/config.ini.template','[general]\r\n\r\n;Database connection settings\r\ndb.adapter=PDO_MYSQL\r\ndb.host=_Engines_Environment(dbhost)\r\ndb.username=_Engines_Environment(dbuser)\r\ndb.password=\"_Engines_Environment(dbpasswd)\"\r\ndb.dbname=_Engines_Environment(dbhost)\r\n\r\n;Security\r\nsecurity.cookie = [:security_cookie]\r\n\r\npath.upload = /home/fs/persistence/upload\r\n;path.logs = /tmp/logs\r\n;path.temp = /tmp/temp\r\n;path.feeds = /tmp/feeds\r\n\r\n;Caching\r\n;cache.content = 1\r\n;cache.metadata = 1\r\n;cache.path = /tmp/cache/\r\n\r\n;Web deployment settings\r\nweb.host=_Engines_Builder(fqdn)\r\nweb.path=[:path]\r\nweb.redirect = 1\r\nweb.timezone=Europe/Brussels\r\n\r\n;Global application settings\r\napp.closed=1							; Set to 0 to enable registration\r\napp.from_email=support@_Engines_Builder(domain_name)\r\napp.admin_email=admin@_Engines_Builder(domain_name)\r\napp.user=admin					; Comment this to switch to multi-user setup\r\n\r\n;Debug settings\r\ndebug 	= 0\r\nprofile = 0\r\n\r\n\r\n;; RSS settings\r\nrss.default.hide_content=1\r\n\r\n;; Flickr settings\r\n[:flickr_comment]flickr.api_key=[:flickr_key]\r\n\r\n;; Twitter settings\r\n;; In order to integrate storytlr with twitter, you need to obtain OAuth application keys \r\n;; from Twitter directly (we are not allowed to redistribute keys). It is a simple process\r\n;; that you can follow here: http://dev.twitter.com/apps\r\n;;\r\n;;twitter.consumer_key=\r\n;;twitter.consumer_secret=\r\n;;twitter.default.hide_replies=1\r\n\r\n;; Seesmic settings\r\nseesmic.default.hide_replies=1\r\n\r\n;; Laconica settings\r\nlaconica.default.hide_replies=1\r\n\r\n;; Youtube settings\r\n;youtube.client_id=\r\n;youtube.developer_key=\r\n\r\n;; Google map\r\n[